package add_day1proj;

public class Add_calculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=2;
		int plus=a+b;
		int minus=a-b;
		double d=2.5;
		float f=12;
		char c='c';
		boolean comment=true;
		String proj="ARITHMETIC";
		System.out.println("project name is :"+proj);
		System.out.println("addition is :"+plus);
		System.out.println("subtraction is :"+minus);
		System.out.println("multiplication is :"+a*b);
		System.out.println("division is :"+a/b);
		System.out.println("modulo is:"+a%b);
		System.out.println("Data Types:");
		System.out.println("int:"+a);
		System.out.println("double:"+d);
		System.out.println("float:"+f);
		System.out.println("char:"+c);
		System.out.println("boolean:"+comment);
		System.out.println("string:"+proj);
		
		
		
		
		
		
		
				
				

	}

}
